mapper.select:
SELECT
FROM data_info u WHERE status = 0 AND data_type=#{dataType}
ORDER BY u.create_time DESC

mapper.insert:
INSERT IGNORE INTO data_info
(
user_id
,file_id
,data_title
,data_num
,data_desc
,data_type
,pub_user
,pub_desc
,status
,create_time
,update_time
)
VALUES
(
#{userId}
,#{fileId}
,#{dataTitle}
,#{dataNum}
,#{dataDesc}
,#{dataType}
,#{pubUser}
,#{pubDesc}
,0
,now()
,now()
)
